using OCMS_Services.IService;
using PuppeteerSharp;
using System;
using System.Collections.Generic;
using System.Drawing.Printing;
using System.Linq;
using System.Net.Http.Headers;
using System.Text;
using System.Text.Json;
using System.Threading.Tasks;

namespace OCMS_Services.Service
{
    public class PdfSignerService : IPdfSignerService
    {
        private readonly HttpClient _httpClient;
        private readonly IHsmAuthService _tokenService;

        public PdfSignerService(HttpClient httpClient, IHsmAuthService tokenService)
        {
            _httpClient = httpClient;
            _tokenService = tokenService;
        }

        private async Task<string> SignPdfBase64Async(string fileDataBase64)
        {
            var token = await _tokenService.GetTokenAsync();
            const string url = "https://demohsm.wgroup.vn/hsm/pdf";

            var body = new
            {
                options = new
                {
                    PAGENO = 1,
                    POSITIONIDENTIFIER = "Vùng dành cho chữ ký số",
                    RECTANGLESIZE = "180,50",
                    RECTANGLEOFFSET = "-50,-40",
                    VISIBLESIGNATURE = true,
                    VISUALSTATUS = false,
                    SHOWSIGNERINFO = true,
                    SIGNERINFOPREFIX = "Ký bởi:",
                    SHOWREASON = true,
                    SIGNREASONPREFIX = "Lý do:",
                    SIGNREASON = "Tôi đồng ý",
                    SHOWDATETIME = true,
                    DATETIMEPREFIX = "Ký ngày:",
                    SHOWLOCATION = true,
                    LOCATIONPREFIX = "Nơi ký:",
                    LOCATION = "Hồ Chí Minh",
                    TEXTDIRECTION = "OVERLAP",
                    TEXTCOLOR = "red",
                    IMAGEANDTEXT = true,
                    BACKGROUNDIMAGE = "",
                    SIGNATUREIMAGE = ""
                },
                file_data = fileDataBase64
            };

            var json = JsonSerializer.Serialize(body);
            var content = new StringContent(json, Encoding.UTF8, "application/json");
            _httpClient.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", token);

            var response = await _httpClient.PostAsync(url, content);
            response.EnsureSuccessStatusCode();
            return await response.Content.ReadAsStringAsync();
        }
        private async Task<byte[]> ConvertHtmlToPdf(string htmlContent)
        {
            // Wrap HTML with fixed size and adjustments
            htmlContent = $$"""
    <!DOCTYPE html>
    <html>
    <head>
        <style>
            @page {
                size: A4;
                margin: 0;
            }
            body {
                margin: 0;
                padding: 0;
                font-size: 10pt;
                width: 100%;
                box-sizing: border-box;
            }
            .pdf-container {
                width: 1123px; /* A4 landscape */
                height: 794px;
                padding: 40px;
                box-sizing: border-box;
                position: relative;
            }
            img {
                max-width: 100%;
                height: auto;
            }
        </style>
    </head>
    <body>
        <div class="pdf-container">
            {{htmlContent}}
        </div>
    </body>
    </html>
    """;

            // Khởi tạo Puppeteer
            await new BrowserFetcher().DownloadAsync();
            var launchOptions = new LaunchOptions { Headless = true };
            using var browser = await Puppeteer.LaunchAsync(launchOptions);
            using var page = await browser.NewPageAsync();

            // Set viewport to ensure proper rendering
            await page.SetViewportAsync(new ViewPortOptions
            {
                Width = 1240,  // ~A4 width in pixels at 150dpi
                Height = 1754, // ~A4 height in pixels at 150dpi
                DeviceScaleFactor = 1.5
            });

            // Đặt nội dung HTML
            await page.SetContentAsync(htmlContent);

            // Optimize PDF settings
            var pdfOptions = new PdfOptions
            {
                Format = PuppeteerSharp.Media.PaperFormat.A4,
                PrintBackground = true,
                MarginOptions = new PuppeteerSharp.Media.MarginOptions
                {
                    Top = "5mm",
                    Bottom = "5mm",
                    Left = "5mm",
                    Right = "5mm"
                },
                Scale = 0.9m // Slightly reduced scale to ensure content fits
            };

            byte[] pdfBytes = await page.PdfDataAsync(pdfOptions);
            return pdfBytes;
        }

        public async Task<string> SignPdfAsync(string htmlContent)
        {
            // Step 1: Convert HTML to PDF
            byte[] pdfBytes = await ConvertHtmlToPdf(htmlContent); // Added 'await'

            // Step 2: Convert PDF to Base64
            string pdfBase64 = Convert.ToBase64String(pdfBytes);

            // Step 3: Sign the PDF
            return await SignPdfBase64Async(pdfBase64);
        }
    }
   

}
